mapper.select:
select a.*,ifnull(b.salesMonth,0) from (SELECT g.id,g.goods_id,p.name,p.name_en,p.type,p.logo,g.gg,g.jg,g.cxjg,g.xsjg,g.sales,g.drp_price,p.divide,p.createtime FROM cool_product_gg g left join cool_product p on g.goods_id = p.id where g.stock > 0
and p.type = #{type,jdbcType=INTEGER}
and (p.name like '%' || #{name,jdbcType=VARCHAR} || '%' or p.name_en like '%' || #{name,jdbcType=VARCHAR} || '%' )
and p.id = #{pId,jdbcType=VARCHAR}
and g.id = #{gId,jdbcType=VARCHAR}
group by ifnull(g.cxjg,g.jg) * p.divide desc
group by ifnull(g.cxjg,g.jg) desc
group by g.sales desc
) a left join (SELECT i.gid,sum(i.count) as salesMonth from cool_order_item i inner join cool_order o on o.order_no = i.order_no where left(o.createtime,7) = left(now(),7) and (o.state=0 or o.state=1 or o.state=2 or o.state=3 or o.state=1 or o.state=11) group by i.gid) b on b.gid = a.id

select a.*,ifnull(b.salesMonth,0) from (SELECT g.id,g.goods_id,p.name,p.name_en,p.type,p.logo,g.gg,g.jg,g.cxjg,g.xsjg,g.sales,g.drp_price,p.divide,p.createtime FROM coon_shop_product s left join cool_product p on p.id = s.p_id left join cool_product_gg g on g.goods_id = p.id where g.stock > 0
and s.s_id = #{sId,jdbcType=VARCHAR}
) a left join (SELECT i.gid,sum(i.count) as salesMonth from cool_order_item i inner join cool_order o on o.order_no = i.order_no where left(o.createtime,7) = left(now(),7) and (o.state=0 or o.state=1 or o.state=2 or o.state=3 or o.state=1 or o.state=11) group by i.gid) b on b.gid = a.id

SELECT * FROM coon_shop_product 
where 1=1 

    and p_id = #{pId,jdbcType=VARCHAR}


    and s_id = #{sId,jdbcType=VARCHAR}
