if ($.fn.datebox){
	$.fn.datebox.defaults.currentText = '今天';
	$.fn.datebox.defaults.closeText = '关闭';
	$.fn.datebox.defaults.okText = '确定';
	$.fn.datebox.defaults.missingMessage = '该输入项为必输项';
	$.fn.datebox.defaults.formatter = function(date){
		var y = date.getFullYear();
		var m = date.getMonth()+1;
		var d = date.getDate();
		return y+'-'+(m<10?('0'+m):m)+'-'+(d<10?('0'+d):d);
	};
	$.fn.datebox.defaults.parser = function(s){
		if (!s) return new Date();
		var ss = s.split('-');
		var y = parseInt(ss[0],10);
		var m = parseInt(ss[1],10);
		var d = parseInt(ss[2],10);
		if (!isNaN(y) && !isNaN(m) && !isNaN(d)){
			return new Date(y,m-1,d);
		} else {
			return new Date();
		}
	};
}
if ($.fn.datetimebox && $.fn.datebox){
	$.extend($.fn.datetimebox.defaults,{
		currentText: $.fn.datebox.defaults.currentText,
		closeText: $.fn.datebox.defaults.closeText,
		okText: $.fn.datebox.defaults.okText,
		missingMessage: $.fn.datebox.defaults.missingMessage
	});
}

$.extend($.fn.validatebox.defaults.rules, {  
     date: {  
         validator: function(value){
			//var reg = /^(\d{4})-(\d{2})-(\d{2})$/;         	
            var reg = /^((((1[6-9]|[2-9]\d)\d{2})-(0?[13578]|1[02])-(0?[1-9]|[12]\d|3[01]))|(((1[6-9]|[2-9]\d)\d{2})-(0?[13456789]|1[012])-(0?[1-9]|[12]\d|30))|(((1[6-9]|[2-9]\d)\d{2})-0?2-(0?[1-9]|1\d|2[0-8]))|(((1[6-9]|[2-9]\d)(0[48]|[2468][048]|[13579][26])|((16|[2468][048]|[3579][26])00))-0?2-29-))(\s(([01]\d{1})|(2[0123])):([0-5]\d):([0-5]\d))?$/;
            return reg.test(value); 
        },  
         message: '请输入正确的日期格式'  
     },
     baseValidate: {  
         validator: function(value){
            var reg = /^\w*$/;
            return reg.test(value); 
        },  
         message: '只能输入英文、数字或下划线'  
     }
});

function formatDate(date)
{
	// 如果date不是yyyy-mm-dd的形式则返回
	if (!date.length || date.length != 10)
	{
		return date;
	}
	return date.substr(0,4) + date.substr(5,2) + date.substr(8,2);
}

function parseDate(s)
{
	// 如果s不是yyyymmdd的形式则返回
	if (!s.length || s.length != 8)
	{
		return s;
	}
	var ss = s.split('-');
	return ss[0] + ss[1] + ss[2];
}