mapper.select:
select u.f_user_id, u.empi_id, u.f_username, u.f_icn, u.f_sex, u.f_birthday, u.f_nation, u.f_tel, u.f_photo, u.f_regaddress, u.f_preaddress, u.f_state, u.f_createdate, u.f_updatedate, u.f_creator, u.f_updator, u.del_flag from EMPI_USER u
u.del_flag = 1 and u.f_state = 1
AND u.f_user_id = #{userId,jdbcType=VARCHAR}
AND u.empi_id = #{empiId,jdbcType=VARCHAR}
AND u.f_username LIKE '%'||#{userName,jdbcType=VARCHAR}||'%'
AND u.f_icn = #{icn,jdbcType=VARCHAR}
AND u.f_sex = #{sex,jdbcType=VARCHAR}
AND u.f_nation LIKE '%'||#{nation,jdbcType=VARCHAR}||'%'
AND u.f_regaddress LIKE '%'||#{regaddress,jdbcType=VARCHAR}||'%'
AND u.f_preaddress LIKE '%'||#{preaddress,jdbcType=VARCHAR}||'%'
AND u.f_creator LIKE '%'||#{creator,jdbcType=VARCHAR}||'%'
AND u.f_updator LIKE '%'||#{updator,jdbcType=VARCHAR}||'%'
order by u.empi_id desc

select count(1) from EMPI_USER u
u.del_flag = 1 and u.f_state = 1
AND u.empi_id = #{empiId,jdbcType=VARCHAR}
AND u.f_username LIKE '%'||#{userName,jdbcType=VARCHAR}||'%'
AND u.f_icn = #{icn,jdbcType=VARCHAR}
AND u.f_sex = #{sex,jdbcType=VARCHAR}
AND u.f_regaddress LIKE '%'||#{regaddress,jdbcType=VARCHAR}||'%'
AND u.f_preaddress LIKE '%'||#{preaddress,jdbcType=VARCHAR}||'%'
AND u.f_creator LIKE '%'||#{creator,jdbcType=VARCHAR}||'%'
AND u.f_updator LIKE '%'||#{updator,jdbcType=VARCHAR}||'%'

select u.f_user_id, u.empi_id, u.f_username, u.f_icn, u.f_sex, u.f_birthday, u.f_nation, u.f_tel, u.f_photo, u.f_regaddress, u.f_preaddress, u.f_state, u.f_createdate, u.f_updatedate, u.f_creator, u.f_updator, u.del_flag, c.card_id, c.card_no, c.card_sid, c.card_type, c.card_state, c.empi_id cempi from EMPI_USER u left outer join EMPI_CARD c on u.empi_id = c.empi_id and c.card_state = 1
u.del_flag = 1 and u.f_state = 1
AND u.f_user_id = #{userId,jdbcType=VARCHAR}
AND u.empi_id = #{empiId,jdbcType=VARCHAR}
AND u.f_username LIKE '%'||#{userName,jdbcType=VARCHAR}||'%'
AND u.f_icn = #{icn,jdbcType=VARCHAR}
AND u.f_sex = #{sex,jdbcType=VARCHAR}
AND u.f_regaddress LIKE '%'||#{regaddress,jdbcType=VARCHAR}||'%'
AND u.f_preaddress LIKE '%'||#{preaddress,jdbcType=VARCHAR}||'%'
AND u.f_creator LIKE '%'||#{creator,jdbcType=VARCHAR}||'%'
AND u.f_updator LIKE '%'||#{updator,jdbcType=VARCHAR}||'%'
