mapper.select:
select count(*)
from B_DICTIONARY
where DICTTYPE_ID = #{dicttypeId,jdbcType=VARCHAR}

SELECT B.DICT_ID, B.DICT_NAME, B.DICT_CODE, B.NAME_PINYIN, B.NAME_JIANPIN, B.REMARK, B.DICTTYPE_ID, B.IS_DELETED, B.ORDER_NO FROM(SELECT B_DICTIONARY.DICT_ID, B_DICTIONARY.DICT_NAME, B_DICTIONARY.DICT_CODE, B_DICTIONARY.NAME_PINYIN, B_DICTIONARY.NAME_JIANPIN, B_DICTIONARY.REMARK, B_DICTIONARY.DICTTYPE_ID, B_DICTIONARY.IS_DELETED, B_DICTIONARY.ORDER_NO FROM B_DICTIONARY
B_DICTIONARY.IS_DELETED = 0 and B_DICTIONARY.DICTTYPE_ID = #{dicttypeId,jdbcType=VARCHAR}
and B_DICTIONARY.ORDER_NO < #{orderNo,jdbcType=DECIMAL}
and B_DICTIONARY.ORDER_NO > #{orderNo,jdbcType=DECIMAL}
order by B_DICTIONARY.ORDER_NO desc
order by B_DICTIONARY.ORDER_NO asc
) B WHERE ROWNUM = 1
