mapper.select:
select 

from P_DS_OBJ t

	t.DEL_FLAG = '1' 

		and t.P_URL LIKE '%' || #{pUrl,jdbcType=VARCHAR} || '%'


		and t.P_URL = #{pUrl,jdbcType=VARCHAR}


		and t.P_USER_NAME = #{pUserName,jdbcType=VARCHAR}

select count(t.ID_) from P_DS_OBJ t

	t.DEL_FLAG = '1' 

		and t.P_URL LIKE '%' || #{pUrl,jdbcType=VARCHAR} || '%'


		and t.P_URL = #{pUrl,jdbcType=VARCHAR}


		and t.P_USER_NAME = #{pUserName,jdbcType=VARCHAR}
