mapper.select:
select a.dept_id, a.dept_name, a.dept_code, a.dept_desc, a.CITY_CODE, a.TOWN_CODE,a.COMMUNITY_CODE,a.MEDICAL_ORG_CODE, a.CLASSIFIED_CODE, a.DEPT_CLASSIFY_CODE,a.HOSPITAL_INTERNAL_CODE,a.CLINIC_INHOSPITAL_CODE, (select t.dept_name from P_RESOURCE_DEPT_INFO t where t.dept_code=a.parent_dept_code
and t.org_code = #{orgCode,jdbcType=VARCHAR}
) as PARENT_DEPT_CODE from P_RESOURCE_DEPT_INFO a where 1=1
and (a.dept_name like '%' || #{deptName,jdbcType=VARCHAR} || '%' or a.dept_code like '%' || #{deptName,jdbcType=VARCHAR} || '%')
and a.org_code = #{orgCode,jdbcType=VARCHAR}

select count(a.dept_id) from P_RESOURCE_DEPT_INFO a where 1=1
and (a.dept_name like '%' || #{deptName,jdbcType=VARCHAR} || '%' or a.dept_code like '%' || #{deptName,jdbcType=VARCHAR} || '%')
and a.org_code = #{orgCode,jdbcType=VARCHAR}

select t.u_id, t.name, (select A.DICT_NAME from P_DICTIONARY A, P_DICTIONARY_TYPE B where A.DICTTYPE_ID = B.TYPE_ID and A.IS_DELETED = 0 and B.STANDARD_CODE = 'GB/T2261.1' and A.DICT_CODE = t.sex_code) as sex_code, (select dept.DEPT_NAME from P_RESOURCE_DEPT_INFO dept where dept.dept_code = t.department_code
and dept.org_code = #{medicalOrganCode,jdbcType=VARCHAR}
) as department_code, (select A.DICT_NAME from P_DICTIONARY A, P_DICTIONARY_TYPE B where A.DICTTYPE_ID = B.TYPE_ID and A.IS_DELETED = 0 and B.STANDARD_CODE = 'EXV00.00.068' and A.DICT_CODE = t.POST_CODE) as POST_CODE, (select A.DICT_NAME from P_DICTIONARY A, P_DICTIONARY_TYPE B where A.DICTTYPE_ID = B.TYPE_ID and A.IS_DELETED = 0 and B.STANDARD_CODE = 'CV08.30.005' and A.DICT_CODE = t.POSITIONAL_TITLES_CODE) as POSITIONAL_TITLES_CODE, t.RESUME from p_resource_user_info t where 1 = 1
and t.name like '%' || #{name,jdbcType=VARCHAR} || '%'
and t.department_code = #{departmentCode,jdbcType=VARCHAR}
and t.medical_organ_code = #{medicalOrganCode,jdbcType=VARCHAR}
and t.post_code = #{postCode,jdbcType=VARCHAR}

select 
   count(*)
  from p_resource_user_info t
 where 1 = 1

   and t.name like '%' || #{name,jdbcType=VARCHAR} || '%'


   and t.department_code = #{departmentCode,jdbcType=VARCHAR}


   and t.medical_organ_code = #{medicalOrganCode,jdbcType=VARCHAR}


   and t.post_code = #{postCode,jdbcType=VARCHAR}

select a.dept_name, a.dept_code from P_RESOURCE_DEPT_INFO a where 1=1
and a.dept_name like '%' || #{name,jdbcType=VARCHAR} || '%'
and a.org_code = #{orgCode,jdbcType=VARCHAR}

select *
  from p_resource_family_info a
 where a.FAMI_ID =  #{familyId,jdbcType=VARCHAR}
